		// 		setColorList(res.data);
		// 	})
		// 	.catch((err) => console.log("Something went wrong:", err));
	}, []);

	return (
		<>
			<ColorList colors={colorList} updateColors={setColorList} />
			<Bubbles data-testid="bubbles" colors={colorList} />
		</>
	);
};

export default BubblePage;
